        let headers = new HttpHeaders({
            'Content-Type': 'application/json'
        });
        console.log(headers);
        return this.http
            .post(this.BASE_URL, data, {
            headers,
        })
            .pipe(map((response) => {
            return response;
        }), catchError(async (error) => console.log(error)));
    }
    getAllGateways() {
        return this.http.get(this.BASE_URL, {
            headers: new HttpHeaders({
                'content-type': 'application/json'
            }),
        });
    }
    getGatewayById(id) {
        return this.http.get(this.BASE_URL + '/gateway?id=' + id, {
            headers: new HttpHeaders({
                'content-type': 'application/json'
            }),
        });
    }
    updateGateway(gatewayData, id) {
        const headers = new HttpHeaders({
            'Content-Type': 'application/json'
        });
        return this.http
            .put(`${this.BASE_URL}/gateway?id=${id}`, gatewayData, { headers })
            .pipe(tap((updatedGatewayDetail) => console.log(`updated gateway detail = ${JSON.stringify(updatedGatewayDetail)}`)), catchError(async (error) => console.log(error)));
    }
    deleteGateway(id) {
        const headers = new HttpHeaders({
            'Content-Type': 'application/json'
        });
        console.log(headers);
        return this.http.delete(`${this.BASE_URL}/gateway?id=${id}`, { headers });
    }
    deleteGatewayDevice(id, deviceId) {
        const headers = new HttpHeaders({
            'Content-Type': 'application/json'
        });
        console.log(headers);
        return this.http.delete(`${this.BASE_URL}/gateway/device?id=${id}&deviceId=${deviceId}`, { headers });
    }
}
